/**
 * @proyect PruebaTecnica
 * @file types/media.d.ts
 * @description Declaraciones de módulos para tipos de archivos de imagen y video
 * @author Guillermo Corredor
 * @created 5/06/2025
 *
 * @note
 * Este archivo contiene declaraciones de módulos para permitir la importación
 * de archivos de imagen y video en TypeScript sin errores de tipo.
 */

/**
 * @declaration jpg
 * @description Permite la importación de archivos JPG
 */
declare module "*.jpg";

/**
 * @declaration JPG
 * @description Permite la importación de archivos JPG (mayúsculas)
 */
declare module "*.JPG";

/**
 * @declaration jpeg
 * @description Permite la importación de archivos JPEG
 */
declare module "*.jpeg";

/**
 * @declaration JPEG
 * @description Permite la importación de archivos JPEG (mayúsculas)
 */
declare module "*.JPEG";

/**
 * @declaration png
 * @description Permite la importación de archivos PNG
 */
declare module "*.png";

/**
 * @declaration PNG
 * @description Permite la importación de archivos PNG (mayúsculas)
 */
declare module "*.PNG";

/**
 * @declaration MOV
 * @description Permite la importación de archivos de video MOV
 */
declare module "*.MOV";
